<?php
class ModelCheckoutRecurring extends Model {
	public function addSubscription($order_id, $description, $data) {
		$this->db->query("INSERT INTO `" . DB_PREFIX . "subscription` SET `customer_id` = '" . (int)$data['customer_id'] . "', `order_id` = '" . (int)$order_id . "', `order_product_id` = '" . (int)$data['recurring']['order_product_id'] . "', `reference` = '', `subscription_plan_id` = '" . (int)$data['recurring']['subscription_plan_id'] . "', `name` = '" . $this->db->escape($data['recurring']['name']) . "', `description` = '" . $this->db->escape($description) . "', `trial_price` = '" . (float)$data['recurring']['trial_price'] . "', `trial_frequency` = '" . $this->db->escape($data['recurring']['trial_frequency']) . "', `trial_cycle` = '" . (int)$data['recurring']['trial_cycle'] . "', `trial_duration` = '" . (int)$data['recurring']['trial_duration'] . "', `trial_remaining` = '" . (int)$data['recurring']['trial_remaining'] . "', `trial_status` = '" . (int)$data['recurring']['trial_status'] . "', `price` = '" . (float)$data['recurring']['price'] . "', `frequency` = '" . $this->db->escape($data['recurring']['frequency']) . "', `cycle` = '" . (int)$data['recurring']['cycle'] . "', `duration` = '" . (int)$data['recurring']['duration'] . "', `remaining` = '" . (int)$data['recurring']['remaining'] . "', `date_next` = NOW(), `subscription_status_id` = '" . (int)$this->config->get('config_subscription_status_id'). "', `status` = '" . (int)$data['recurring']['status'] . "', `date_added` = NOW(), `date_modified` = NOW()");						

		return $this->db->getLastId();
	}
}